tPoint_value;var myProcAndPromille_value;//---var myFootnoteGroup_value;var grekCharStyles_value;var slitno_value;var footnote1_value;var mySpatium_value;var s_options_enabled;////----var tireAndSpatium_value;var mySpaceListBefore_selection;var mySpaceListAfter_selection;var commatire_value;var myCommaAndTire_selection;var shortword_value;var myShortWordSpace_selection;var initials_value;var myASPushkinFirstSpace_selection;var myASPushkinSecondSpace_selection;var myPushkinASFirstSpace_selection;var myPushkinASSecondSpace_selection;var myPhone_value;var p_options_enabled;var sevendigits_value;var sixdigits_value;var myPrevSelectedStyle;var mySlider_value;var mySet34_value;var mySet322_value;var SepIsSpace_value;var SepIsDefis_value;var SepIsMinus_value;var SearchedDefis_value;var SearchedMinus_value;var SearchedTire_value;var SepIsDot_value;var myPrevSelectedStyle;var mySelectedQuotes_value;var setquotes_value;var myStyledText = 0;////===================// Переменные, отражающие выбранные параметры работы скрипта// если переменная равна 2, значит, этот параметр сейчас исключён из рассмотрения.// 1 означает, что флажок установлен, 0 -- сброшенvar myCleanValueFromMenu;var repCharFromMenu;var tabs2spacesFromMenu;var hyphensFromMenu;var bull2textFromMenu;var charStylesFromMenu;var grekCharStylesFromMenu;var digAndCharFromMenu;var rusLangFromMenu;var myTypographicaValueFromMenu;var hyplinksFromMenu;var fixOneLetterFromMenu;var fixTwoLetterFromMenu;var fixDigitAndWordFromMenu;var commonTireFromMenu;var minusFromMenu;var mlnWithoutPointFromMenu;var slitnoFromMenu;var footnote1FromMenu;var ParaIndexFromMenu;var tireAndSpatiumFromMenu;var mySpaceListBeforeFromMenu; // indexvar mySpaceListAfterFromMenu;  // indexvar commatireFromMenu;var myCommaAndTireFromMenu;  // indexvar shortwordFromMenu;var myShortWordSpaceFromMenu; // indexvar initialsFromMenu;var myASPushkinFirstSpaceFromMenu; // indexvar myASPushkinSecondSpaceFromMenu; // indexvar myPushkinASFirstSpaceFromMenu;  // indexvar myPushkinASSecondSpaceFromMenu;  // indexvar mySelectedQuotesFromNenu; // indexvar sevendigitsFromMenu;var sixdigitsFromMenu;var mySliderValueFromMenu;var Set34FromMenu;var SepIsSpaceFromMenu;var setquotes_valueFromMenu;var SepIsDefisFromMenu;var SepIsMinusFromMenu;var SepIsDotFromMenu;var mySpatiumValueFromMenu;var myPhoneValueFromMenu;var myOneLineIsOneAbzatzFromMenu;var SearchedDefisFromMenu;var SearchedMinusFromMenu;var SearchedTireFromMenu;var myCurFileWordsAndUnits = "WordsAndUnits.jsx";////======////////////////////////////////////// проверка версии InDesignif (parseInt (app.version) < 6) { // app.version	alert ("Запустите InDesign CS4+ для обработки текста этим скриптом.", myProgramTitul);    exit();} // app.versionif(app.documents.length == 0) { // app.documents.length != 0	alert("Нет открытых документов.", myProgramTitul);		exit();}var mySelection = app.selection[0];var myDocument = app.activeDocument;// масиив объектов внутри которых обрабатывается текстvar processingObjects = [];/// <<< на время отладки...var myWordsAndUnitsiFile = myFile(myCurFileWordsAndUnits);var myIniFile = new File (myWordsAndUnitsiFile);if (myIniFile.exists) {	// прочитаем значения переменных из файла WordsAndUnits	try { 		app.doScript(myIniFile);	} catch (e) {		alert('Синтаксическая ошибка в файле WordsAndUnits.jsx\n' + e.message);		exit();	}} else {	alert("Не найден файл WordsAndUnits.jsx. Он должен быть в \том же каталоге, где размещен этот скрипт.");	exit(); }   /// <<< на время отладки...if(app.selection.length == 0) { // app.selection.length != 0	alert("Перед запуском программы поставьте курсор в текст для подготовки всей \статьи к верстке.\nДля обработки части текста выделите его фрагмент.", 		myProgramTitul);		exit();}// поиск подходящих объектов внутри выделения(function seekStories(items) {	for (var i = items.length; i--; ) {		var item = items[i];		//alert(item.constructor.name);				// анализируем что же выделил пользователь?		switch (item.constructor.name) {			case 'InsertionPoint': // просто курсор внутри текста - не выделено ничего			case 'TextFrame':      // выделен весь текстовый фрейм целиком				seekStories([item.parentStory]);				break;						// выделен фрагмент			case 'Character':			case 'Line':			case 'Text':			case 'TextColumn':			case 'TextStyleRange':			case 'Word':				// расширим выделенную область до кратности абзацам				if (item.paragraphs.length > 0) {					var myStartOfFragment = item.paragraphs.firstItem().characters.firstItem().index;					var myEndOfFragment = item.paragraphs.lastItem().characters.lastItem().index;					item = item.parent.characters.itemByRange(myStartOfFragment, myEndOfFragment);				}				// внимание! дальше выполняется следующий блок case						case 'Paragraph':			case 'Story':				// пропустим пустые				if (item.contents.length > 0) {					processingObjects.push(item);				}				seekStories(item.tables);				// добавим вложенные				seekStories(item.textFrames);				break;						// таблица			case 'Table':				// в таблице обработаем каждую ячейку				seekStories(item.cells);				break;							case 'Cell':				// обработаем все объекты Text				seekStories(item.texts);				break;		}	}})(app.selection);// выход если ничего подходящего не выделеноif (0 == processingObjects.length) {	alert("Перед запуском программы поставьте курсор в текст для подготовки всей статьи к верстке.\nВыделите часть текста для проверки только её.", myProgramTitul);		exit();}//~ // #DoTextOK.dtok - основной файл обработки текстаvar mySetFile;var myScriptFile = myGetScriptPath();var myScriptFolder = decodeURI(myScriptFile.path);var myFilePath = decodeURI(myScriptFolder + "/ " + myDefSetName); //  myDefSetName = #DoTextOK.dtokvar mySetInfoFile = new File (myFilePath);myScrFolder = new Folder (myScriptFile.path);var myDoTextOKList = myScrFol